mapper.select:
select count(1) as count from event_group where event_group_id in ( select event_group.event_group_id from event_group left join event_group_relations on event_group_relations.event_group_id = event_group.event_group_id left join event_dic on event_dic.event_id = event_group_relations.event_id where 1=1
and event_group.event_group_id = #{event_group_id}
and event_group.event_group_name LIKE CONCAT('%', #{event_group_name}, '%')
and event_dic.event_id = #{event_id}
group by event_group.event_group_id )

select event_group.*,event_dic.event_id,CASE WHEN event_name_cn is NULL THEN event_name_en ELSE CONCAT(event_name_cn,'（',event_name_en,'）') END as event_name_en,event_name_cn,event_type from event_group left join event_group_relations on event_group_relations.event_group_id = event_group.event_group_id left join event_dic on event_dic.event_id = event_group_relations.event_id where event_group.event_group_id in (select * from ( select event_group.event_group_id from event_group left join event_group_relations on event_group_relations.event_group_id = event_group.event_group_id left join event_dic on event_dic.event_id = event_group_relations.event_id where 1=1
and event_group.event_group_id = #{event_group_id}
and event_group.event_group_name LIKE CONCAT('%', #{event_group_name}, '%')
and event_dic.event_id = #{event_id}
group by event_group.event_group_id
limit #{startRecord},#{pageSize}
) as event_group_ids)
